import React from "react";
import $ from "jquery";
import "../css/CoinStats.css";

export default function CoinStats() {
	return (
        <div>
            <p>
                { hashrate }
            </p>
        </div>
	);
}

// Explorer Stats
$(document).ready(function () {
	// Fetch the initial table
	refreshTable();

	// Fetch every 5 seconds
	setInterval(refreshTable, 24000);
});

function refreshTable() {
	const request = new XMLHttpRequest();

	request.open("GET", "https://explorer.xwp.one/api/networkinfo");
	request.send();

	request.onload = () => {
		if (request.status === 200) {
			console.log("Success"); // So extract data from json and create table

			//Extracting data
			var hashrate = JSON.parse(request.response).data.hash_rate;
			var difficulty = JSON.parse(request.response).data.difficulty;

			//Creating table
			var network2 =
				"<div id='#networkdivload' class='mx-1 my-1'><i class='fas fa-tachometer-alt'></i>&ensp;Network " +
				(hashrate / 32).toFixed(2) +
				" Kgps</div><div class='mx-1 my-1'><i class='fas fa-unlock'></i>&ensp;Difficulty " +
				Intl.NumberFormat().format(difficulty) +
				"</div>";
		}
	};
}

// Explorer Stats 2
$(document).ready(function () {
	// Fetch the initial table
	refreshTable2();

	// Fetch every 5 seconds
	setInterval(refreshTable2, 24000);
});

function refreshTable2() {
	const request2 = new XMLHttpRequest();

	request2.open("GET", "https://explorer.xwp.one/api/emission");
	request2.send();

	request2.onload = () => {
		if (request2.status === 200) {
			console.log("Success"); // So extract data from json and create table

			//Extracting data
			var coinbase = JSON.parse(request2.response).data.coinbase;
			var blocknumber = JSON.parse(request2.response).data.blk_no;

			console.log(new Intl.NumberFormat().format(coinbase));

			//Creating table
			var network3 =
				"<div class='mx-1 my-1'><i class='fas fa-layer-group'></i>&ensp;Circulation " +
				Intl.NumberFormat().format(
					(coinbase / 1000000000000).toFixed(0)
				) +
				" XWP</div><div class='mx-1 my-1'><i class='fas fa-cubes'></i>&ensp;Block " +
				Intl.NumberFormat().format(blocknumber) +
				"</div>";
		}
	};
}
