import React from "react";
import $ from "jquery";
import "../css/CoinStats.css";

export default function CoinStats() {
	return (
		<div className="columns coinStats">
			<div id="networkDiv" class="column"></div>
			<div id="networkDiv2" class="column"></div>
			<div id="tradeogreDiv" class="column"></div>
		</div>
	);
}

// Explorer Network and Difficulty
$(document).ready(function () {
	// Fetch the initial table
	refreshTable();

	// Fetch every 5 seconds
	setInterval(refreshTable, 24000);
});

function refreshTable() {
	const request = new XMLHttpRequest();

	request.open("GET", "https://explorer.xwp.one/api/networkinfo");
	request.send();

	request.onload = () => {
		if (request.status === 200) {
			console.log("Success"); // So extract data from json and create table

			//Extracting data
			var hashrate = JSON.parse(request.response).data.hash_rate;
			var difficulty = JSON.parse(request.response).data.difficulty;

			//Creating table
			var network2 =
				`<div>
                    <div>
                        Network
                    </div>
                    <div>`
                        + (hashrate / 32).toFixed(2) + ` Kgps
                    </div>
                </div>
                <div class="column">
                    <div>
                        Difficulty
                    </div>
                    <div>`
                        + Intl.NumberFormat().format(difficulty) +`
                    </div>
                </div>`;

			//Showing the table inside table
			document.getElementById("networkDiv").innerHTML = network2;
		}
	};
}

// Explorer Stats Circulation and Block
$(document).ready(function () {
	// Fetch the initial table
	refreshTable2();

	// Fetch every 5 seconds
	setInterval(refreshTable2, 24000);
});

function refreshTable2() {
	const request2 = new XMLHttpRequest();

	request2.open("GET", "https://explorer.xwp.one/api/emission");
	request2.send();

	request2.onload = () => {
		if (request2.status === 200) {
			console.log("Success"); // So extract data from json and create table

			//Extracting data
			var coinbase = JSON.parse(request2.response).data.coinbase;
			var blocknumber = JSON.parse(request2.response).data.blk_no;

			console.log(new Intl.NumberFormat().format(coinbase));

			//Creating table
			var network3 =
				`<div>
                    <div>
                        Circulation
                    </div>
                    <div>`
                        + Intl.NumberFormat().format((coinbase / 1000000000000).toFixed(0)) +` 
                        XWP
                    </div>
                </div>
                <div>
                    <div>
                        Block
                    </div>
                    <div>` 
                        + Intl.NumberFormat().format(blocknumber) + `
                    </div>
                </div>`;

			//Showing the table inside table
			document.getElementById("networkDiv2").innerHTML = network3;
		}
	};
}

// Explorer Network and Difficulty
$(document).ready(function () {
	// Fetch the initial table
	refreshTable3();

	// Fetch every 5 seconds
	setInterval(refreshTable3, 24000);
});

function refreshTable3() {
	const request = new XMLHttpRequest();

	request.open("GET", "https://tradeogre.com/api/v1/ticker/BTC-XWP");
	request.send();

	request.onload = () => {
		if (request.status === 200) {
			console.log("Success"); // So extract data from json and create table

			//Extracting data
			var price = JSON.parse(request.response).price;
			var volume = JSON.parse(request.response).volume;

			//Creating table
			var network2 =
				`<div>
                    <div>
                        Price
                    </div>
                    <div>
                    ` + price + ` Sats
                    </div>
                </div>
                <div>
                    <div>
                        Volume
                    </div>
                    <div>
                    ` + volume + ` BTC
                    </div>
                </div>`;

			//Showing the table inside table
			document.getElementById("tradeogreDiv").innerHTML = network2;
		}
	};
}
