import React, { Component } from "react";
import axios from "axios";
import "../css/CoinStats.css";

export default class CoinData extends Component {
	componentDidMount() {
		axios.all([
			axios.get("https://explorer.xwp.one/api/networkinfo"),
            axios.get("https://explorer.xwp.one/api/emission"),
            axios.get("https://tradeogre.com/api/v1/ticker/BTC-XWP")
            ])
			.then(responseArr => {
                this.setState({ hashrate: responseArr[0].data.data.hash_rate });
                this.setState({ difficulty: responseArr[0].data.data.difficulty });
                this.setState({ coinbase: responseArr[1].data.data.coinbase });
                this.setState({ blocknumber: responseArr[1].data.data.blk_no });
                this.setState({ price: responseArr[2].data.price });
                this.setState({ volume: responseArr[2].data.data.volume });
                console.log(responseArr[1].data);
			})
			.catch((error) => {
				console.log(error);
			});
	}
	constructor(props) {
		super(props);
		this.state = {
			hashrate: "",
            difficulty: "",
            coinbase: "",
            blocknumber: "",
            price: "",
            volume: ""
		};
	}
	render() {
        const { hashrate, difficulty, coinbase, blocknumber, price, volume } = this.state;
		return (
			<div className="columns coinStats">
				<div class="column is-flex"></div>
                <div class="column is-2">
                    <div class="column">
                        <div>
                            Network
                        </div>
                        <div class="coinStatsContent">
                            {(hashrate / 32).toFixed(2)} Kgps
                        </div>
                    </div>
                    <div class="column">
                        <div>
                            Difficulty
                        </div>
                        <div class="coinStatsContent">
                            {Intl.NumberFormat().format(difficulty)}
                        </div>
                    </div>
                </div>
                <div class="column is-2">
                    <div class="column">
                        <div>
                            Circulation
                        </div>
                        <div class="coinStatsContent">
                            {Intl.NumberFormat().format((coinbase / 1000000000000).toFixed(0))} 
                            XWP
                        </div>
                    </div>
                    <div class="column">
                        <div>
                            Block
                        </div>
                        <div class="coinStatsContent"> 
                            {Intl.NumberFormat().format(blocknumber)}
                        </div>
                    </div>
                </div>
				<div class="column is-2">
                    <div class="column">
                        <div>
                            Price
                        </div>
                        <div class="coinStatsContent">
                        {(price * 100000000).toFixed(0)} Sats
                        </div>
                    </div>
                    <div class="column">
                        <div>
                            Volume
                        </div>
                        <div class="coinStatsContent">
                        {(volume / 1).toFixed(3)} BTC
                        </div>
                    </div>
                </div>
				<div class="column is-flex"></div>
			</div>
		);
	}
}
