import React, { Component } from "react";
import axios from "axios";
import "../css/CoinStats.css";

export default class CoinData extends Component {
	componentDidMount() {
		axios.all([
			axios.get("https://explorer.xwp.one/api/networkinfo"),
            axios.get("https://explorer.xwp.one/api/emission"),
            axios.get("https://tradeogre.com/api/v1/ticker/BTC-XWP")
            ])
			.then(responseArr => {
                this.setState({ hashrate: response.data.data.hash_rate });
                this.setState({ difficulty: response.data.data.difficulty });
                this.setState({ coinbase: response.data.data.coinbase });
                this.setState({ blocknumber: response.data.data.blk_no });
                this.setState({ price: response.data.data.price });
                this.setState({ volume: response.data.data.volume });
                console.log(response.data);
			})
			.catch((error) => {
				console.log(error);
			});
	}
	constructor(props) {
		super(props);
		this.state = {
			hashrate: "",
            difficulty: "",
		};
	}
	render() {
        const { hashrate, difficulty } = this.state;
		return (
			<div className="columns coinStats">
				<div class="column is-flex"></div>
                <div><h1>{hashrate} H/s</h1></div>
                <div><h1>{difficulty} H/s</h1></div>
				<div class="column is-2"></div>
				<div class="column is-2"></div>
				<div class="column is-flex"></div>
			</div>
		);
	}
}
